import React from "react";
import "./App.css";
import { wordsArray } from "./data/words";
import { useState } from "react";
import { time } from "console";

function App() {
  const [current, setCurrent] = useState<any>(
    wordsArray[Math.floor(Math.random() * wordsArray.length)]
  );
  const [value, setValue] = useState<any>(null);
  const [score, setScore] = useState<number>(0);
  const [timer, setTimer] = useState<number>(60);
  const [gameStatus, setGameStatus] = useState<boolean>(false);



  return (
    <div className="App">
      <header>GetWord</header>
      <h1>score: 10</h1>
      <div className="gameContainer">
        <div className="timer">{timer}</div>
        <label className="word">{current}</label>
        <input className="input" placeholder="type to start game" value={value} onChange={(event) => {
          setValue(event.target.value)
        }}></input>
        <button className="restartBtn">Reset</button>
      </div>
    </div>
  );
}

export default App;
